mapper.select:
MERGE INTO PICNIC_LIST USING DUAL ON (key = #{key})
WHEN MATCHED THEN UPDATE SET NAME=#{place_name}, PLACE=#{address_name}, ROAD_PLACE=#{road_address_name}, PHONE=#{phone}, X=#{x}, Y=#{y}, CATEGORY=#{category_code}
WHEN NOT MATCHED THEN INSERT(NAME, PLACE, ROAD_PLACE, PHONE, X, Y, KEY, CATEGORY) VALUES(#{place_name}, #{address_name}, #{road_address_name}, #{phone}, #{x}, #{y}, #{key}, #{category_code})
