mapper.select:
MERGE INTO PICNIC_LIST USING DUAL ON (KEY = #{KEY})
WHEN MATCHED THEN UPDATE SET NAME=#{place_name}, PLACE=#{address_name}, ROAD_PLACE=#{road_address_name}, PHONE=#{phone}, X=#{x}, Y=#{y}, CATEGORY=#{categori_code}
WHEN NOT MATCHED THEN INSERT(NAME, PLACE, ROAD_PLACE, PHONE, X, Y, KEY, CATEGORI) VALUES(#{place_name}, #{address_name}, #{road_address_name}, #{phone}, #{x}, #{y}, #{key}, #{categori_code})
